mapper.select:
SELECT MENU_PATH , PATH FROM (WITH RECURSIVE HIERARCHY_MENU AS ( SELECT MENU_NO , TITLE , DESCRIPT , FORDER_YN , CONCAT(MENU_NO) AS PATH , CONCAT(TITLE) AS MENU_PATH , 0 AS LVL , DEPTH , PARENT_NO FROM MENU WHERE PARENT_NO=0 AND ID = #{id}
UNION ALL SELECT M.MENU_NO , M.TITLE , M.DESCRIPT , M.FORDER_YN , CONCAT(HM.PATH ,">",M.MENU_NO) AS PATH , CONCAT(HM.MENU_PATH ,">",M.TITLE) AS MENU_PATH , HM.LVL+1 AS LVL , M.DEPTH , M.PARENT_NO FROM HIERARCHY_MENU HM JOIN MENU M ON HM.MENU_NO = M.PARENT_NO AND M.USE_YN = 'Y'
) SELECT MENU_NO , MENU_PATH , PATH FROM HIERARCHY_MENU ORDER BY PATH) MENU_PATH WHERE MENU_NO = #{menuNo}

mapper.insert:
INSERT INTO MENU(
					MENU_NO
					, ID
					, TITLE
					, DESCRIPT
					, PARENT_NO
				)
		VALUES
				(
					NEXT_VAL('MENU')
					, #{id}
					, #{title}
					, #{descript}
					, #{parentNo}
				)

mapper.update:
UPDATE	MENU
SET		USE_YN = 'N'
WHERE	MENU_NO = #{menuNo}
	AND	ID = #{id}

UPDATE	MENU
SET		PARENT_NO = #{moveNo} 
		, TITLE = #{title} 
		, DESCRIPT = #{descript} 
WHERE	MENU_NO = #{crrentNo}
	AND	ID = #{id}
