mapper.insert:
INSERT INTO WORD
		(
			ORG_WORD
			, CONV_WORD
			, ID
		)
		VALUES
		(
			#{orgWord}
			, #{convWord}
			, #{id}
		)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE	WORD 
SET		WRONG_COUNT = WRONG_COUNT+1
WHERE 	ID = #{id}
	AND	WORD_NO = #{wordNo}

UPDATE	WORD 
SET		HIT_YN = #{hitYn}
WHERE 	ID = #{id}
	AND	WORD_NO = #{wordNo}
